targets.expr:
(sum(rate(
    tikv_yatp_pool_schedule_wait_duration_sum
    {k8s_cluster="$k8s_cluster",tidb_cluster="$tidb_cluster",instance=~"$instance"}
    [$__rate_interval]
)) by (name, priority)  / sum(rate(
    tikv_yatp_pool_schedule_wait_duration_count
    {k8s_cluster="$k8s_cluster",tidb_cluster="$tidb_cluster",instance=~"$instance"}
    [$__rate_interval]
)) by (name, priority) )

